        this.setState({
            valor: this.state.valor - this.state.passo
        })
    }

    mudarPasso = (novoPasso) => {
        this.se
    }

    render() {
        return (
            <div className="Contador">
                <h2>Contador</h2>

                <PassoForm passo={this.state.passo}
                    onPassoChange={mudarPasso} />

                <Display />

                <div>
                    <button onClick={this.inc}>+</button>
                    <button onClick={this.dec}>-</button>
                </div>
            </div>
        )
    }
}
